catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	var str = g_form.getValue('request_type');

	if(str == 'new'){
		g_form.setDisplay('request_item', true);
		g_form.setLabelOf('request_item', '모델명');
		g_form.setDisplay('currency', true);
		g_form.setDisplay('description', true);
		g_form.setLabelOf('short_description', '사용목적');
		g_form.setLabelOf('description', '추가 요청사항');

		g_form.setDisplay('rent_date_start', false);
		g_form.setDisplay('rent_date_end', false);
		g_form.setDisplay('return_date', false);
		g_form.setDisplay('asset_item', false);
		g_form.setDisplay('date_of_theft', false);
		g_form.setDisplay('place_of_theft', false);
		g_form.setDisplay('the_case', false);

	}else if(str == 'rent'){
		g_form.setDisplay('rent_date_start', true);
		g_form.setDisplay('rent_date_end', true);
		g_form.setDisplay('currency', true);
		g_form.setDisplay('description', true);
		g_form.setDisplay('short_description', true);
		g_form.setLabelOf('short_description', '사용목적');
		g_form.setLabelOf('description', '추가 요청사항');
		g_form.setDisplay('request_item', true);
		g_form.setLabelOf('request_item', '모델명');

		g_form.setDisplay('return_date', false);
		g_form.setDisplay('asset_item', false);
		g_form.setDisplay('date_of_theft', false);
		g_form.setDisplay('place_of_theft', false);
		g_form.setDisplay('the_case', false);

	}else if(str == 'return'){
		g_form.setDisplay('return_date', true);
		g_form.setDisplay('asset_item', true);
		g_form.setDisplay('short_description', true);
		g_form.setLabelOf('short_description', '반납사유');
		g_form.setDisplay('request_item', true);
		g_form.setLabelOf('request_item', '모델명');

		g_form.setDisplay('rent_date_start', false);
		g_form.setDisplay('rent_date_end', false);
		g_form.setDisplay('description', false);
		g_form.setDisplay('date_of_theft', false);
		g_form.setDisplay('place_of_theft', false);
		g_form.setDisplay('the_case', false);
		g_form.setDisplay('currency', false);

	}else if(str == 'replace'){
		g_form.setDisplay('asset_item', true);
		g_form.setDisplay('short_description', true);
		g_form.setDisplay('description', true);
		g_form.setLabelOf('short_description', '변경사유');
		g_form.setLabelOf('description', '추가 요청사항');
		g_form.setDisplay('request_item', true);
		g_form.setLabelOf('request_item', '새 모델명');

		g_form.setDisplay('rent_date_start', false);
		g_form.setDisplay('rent_date_end', false);
		g_form.setDisplay('return_date', false);
		g_form.setDisplay('date_of_theft', false);
		g_form.setDisplay('place_of_theft', false);
		g_form.setDisplay('the_case', false);
		g_form.setDisplay('currency', false);

	}else if(str == 'lost'){
		g_form.setDisplay('asset_item', true);
		g_form.setDisplay('short_description', true);
		g_form.setDisplay('description', true);
		g_form.setLabelOf('short_description', '변경사유');
		g_form.setLabelOf('description', '추가 요청사항');
		g_form.setDisplay('request_item', true);
		g_form.setLabelOf('request_item', '새 모델명');

		g_form.setDisplay('rent_date_start', false);
		g_form.setDisplay('rent_date_end', false);
		g_form.setDisplay('return_date', false);

		//g_form.setDisplay('cl9mi7c6a000q3b7v0hdf3n5z', true);
		g_form.setDisplay('date_of_theft', true);
		g_form.setDisplay('place_of_theft', true);
		g_form.setDisplay('the_case', true);
		g_form.setDisplay('currency', false);
	}
}
