catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	if(g_form.getValue('rent_date_end') == ''){
		g_form.setMandatory('rent_date_end', true);
		return;
	}
	//종료 날짜는 시작 날짜보다 미래여야 한다.
	if(g_form.getValue('rent_date_end') < g_form.getValue('rent_date_start')){
		g_form.showFieldMsg('rent_date_end','종료 날짜는 시작 날짜보다 미래여야 한다.', 'error',true);
		return;
	}




	var ga = new GlideAjax('x_tigar_office_a_0.Global_DateTime_Utils');
	var currentstart = g_form.getValue('rent_date_start'); //시작날짜 파라미터
	jslog("currentstart : " + currentstart);

	ga.addParam('sysparm_name', 'addDaysDateTime'); //함수명
	ga.addParam('startDateTime', currentstart); //파라미터 전달
	ga.addParam('days', 365*3); //파라미터 전달
	ga.getXML(callback);
}
function callback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	//alert(answer);

	if(g_form.getValue('rent_date_end') > answer){ // 큰 쪽이 미래를 의미
		g_form.setMandatory('rent_date_end', true);
		g_form.showFieldMsg('rent_date_end','최대 대여 기간 : '+answer,'error',true);
	}else{
		g_form.setMandatory('rent_date_end', false);
		g_form.showFieldMsg('rent_date_end','최대 대여 기간 : '+answer,'info',true);
	}
}
