sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var currentDate = g_form.getValue("date_of_theft");
	var today_date = new Date();
	var today_date_time_str = formatDate(today_date, g_user_date_time_format);

	if(currentDate > today_date_time_str){
		g_form.showFieldMsg("date_of_theft","값이 오늘보다 미래입니다.","error");
	}
}
