sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var ga = new GlideAjax('x_tigar_office_a_0.OA_Lookup_User_Info');
	var currentUserID = g_form.getValue('assigned_to'); //sys_id를 반환
	jslog("LHH : " + currentUserID);
	ga.addParam('sysparm_name', 'getUserInfo');
	ga.addParam('userID', currentUserID);
	ga.getXML(callback);
}
function callback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	//alert(answer);
	var stringArray = answer.split('/', 3);
	g_form.setValue('app_department',stringArray[0]);
	g_form.setValue('app_employee_number',stringArray[1]);
	g_form.setValue('app_phone_number',stringArray[2]);
}
