sys_script_client.script:
function onLoad() {
	if(g_form.getValue('state') == '1'){
		//g_form.setValue('requestor',g_user.userID);
		jslog("신청자 로드 업데이트");
		var ga = new GlideAjax('x_tigar_office_a_0.OA_Lookup_User_Info');
		var currentUserID = g_form.getValue('requestor'); //sys_id를 반환
		//jslog("LHH : " + currentUserID);
		ga.addParam('sysparm_name', 'getUserInfo');
		ga.addParam('userID', currentUserID);
		ga.getXML(callback);
	}
}
function callback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	//alert(answer);

	var stringArray = answer.split('/', 3);
	g_form.setValue('req_department',stringArray[0]);
	//g_form.setValue('req_employee_number',stringArray[1]);
	//g_form.setValue('req_phone_number',stringArray[2]);
}
