sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var currentState = g_form.getValue('state');
   if( currentState > 0 && currentState >= 2 ){
	   // 제출 이후 요청 유형 및 상세 정보 수정 불가능
	   g_form.setReadOnly('requested_by', true);
	   g_form.setReadOnly('req_department', true);
	   g_form.setReadOnly('app_department', true);
	   g_form.setReadOnly('request_type', true);
	   g_form.setReadOnly('rent_date_start', true);
	   g_form.setReadOnly('rent_date_end', true);
	   g_form.setReadOnly('oa_type', true);
	   g_form.setReadOnly('monitor_display_size', true);
	   g_form.setReadOnly('labtop_dispaly_size', true);
	   g_form.setReadOnly('phone_type', true);
	   g_form.setReadOnly('request_item', true);
	   g_form.setReadOnly('currency', true);
	   g_form.setReadOnly('short_description', true);
	   g_form.setReadOnly('description', true);
   }else if( currentState < 0 ){ 
	   // 승인 거절, 보류 상태에서는 요청 유형 및 상세 정보 수정 가능
	   g_form.setReadOnly('request_type', false);
	   g_form.setReadOnly('rent_date_start', false);
	   g_form.setReadOnly('rent_date_end', false);
	   g_form.setReadOnly('oa_type', false);
	   g_form.setReadOnly('monitor_display_size', false);
	   g_form.setReadOnly('labtop_display_size', false);
	   g_form.setReadOnly('phone_type', false);
	   g_form.setReadOnly('request_item', false);
	   g_form.setReadOnly('currency', false);
	   g_form.setReadOnly('short_description', false);
	   g_form.setReadOnly('description', false);
   }
}
