sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var documentType = g_form.getValue('document_type');
	if(g_form.getValue('state') == '1'){
		if(documentType == "경조금지급신청서" || documentType == "거래처경조금신청서" || documentType == "제증명발급신청서"){
			g_form.setValue('in_service_life', '10년');
			g_form.setValue('security_level', 's 등급');
		}else if(documentType == "명함신청서" || documentType == "기안품의서"){
			g_form.setValue('in_service_life', '5년');
			g_form.setValue('security_level', 'a 등급');
		}else if(documentType == "도서신청서" || documentType == "oa장비신청서" || documentType == "휴가신청서"){
			g_form.setValue('in_service_life', '3년');
			g_form.setValue('security_level', 'b 등급');
		}else if(documentType == "출장신청서" || documentType == "휴일근무대장"){
			g_form.setValue('in_service_life', '1년');
			g_form.setValue('security_level', 'c 등급');	
		}
	}
}
