sys_script_include.script:
var Global_DateTime_Utils = Class.create();
Global_DateTime_Utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	addDaysDateTime:function(){
		var returnValue=""; // 소속/사번/전화번호
		var startDT = this.getParameter('startDateTime');
		var addDays = this.getParameter('days');
		gs.info('Global_DateTime_Utils - addDaysDateTime 사용');
		///어어어엉
		var gdt = new GlideDateTime(startDT);
		gdt.addDaysLocalTime(addDays); //3년 추가
		returnValue = gdt.getDate();

		return returnValue;
	},
    type: 'Global_DateTime_Utils'
});
